function share(a){var b;switch(a){case"facebook":b="http://www.facebook.com/share.php?u="+URL+"&title="+TITLE;break;case"twitter":b="http://twitter.com/home?status="+TITLE+"+"+URL;break;case"google+":b="https://plus.google.com/share?url="+URL}var c=(H-250)/2,d=(W-500)/2;window.open(b,"Dash","width=500, height=250, top="+c+", left="+d+" scrollbars=yes, status=no, toolbar=no, location=no, directories=no, menubar=no, resizable=no, fullscreen=no")}var TITLE="Try to be as fast as you can in Dash!",URL="http://breno.io/dash",W,H;$(document).ready(function(){$("a.fb").click(function(){share("facebook")}),$("a.tw").click(function(){share("twitter")}),$("a.gp").click(function(){share("google+")}),$("#btn_howto").click(function(){$("#info").fadeIn()}),$("#dismiss").click(function(){$("#info").fadeOut()}),W=$(window).width(),H=$(window).height()});